package ForLoop;

public class NestedForDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int row=1;row<=3;row++)
        {
        	for(int col=1;col<=1;col++)
        	{
        		System.out.print("A"+"B"+"C");
        	}
          System.out.println();
        }
	}

}
